// src/UserList.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';

function UserList() {
  const [users, setUsers] = useState([]);
  const [sortOrder, setSortOrder] = useState('asc');  // Default sort order is ascending

  // Fetch users data from the backend
  useEffect(() => {
    axios.get('http://localhost:3005/users')
      .then((response) => {
        setUsers(response.data);  // Store the response data (users)
      })
      .catch((error) => {
        console.error('There was an error fetching the users:', error);
      });
  }, []);

  // Function to handle sorting by column
  const handleSort = (field) => {
    const newSortOrder = sortOrder === 'asc' ? 'desc' : 'asc';
    setSortOrder(newSortOrder);

    const sortedUsers = [...users].sort((a, b) => {
      if (a[field] < b[field]) {
        return newSortOrder === 'asc' ? -1 : 1;
      }
      if (a[field] > b[field]) {
        return newSortOrder === 'asc' ? 1 : -1;
      }
      return 0;
    });

    setUsers(sortedUsers);
  };

  // Function to handle user deletion
  const handleDelete = (id) => {
    // Make an API call to delete the user by ID
    axios.delete(`http://localhost:3005/users/${id}`)
      .then((response) => {
        // Remove the deleted user from the local state
        setUsers(users.filter(user => user.id !== id));
        alert('User deleted successfully');
      })
      .catch((error) => {
        console.error('There was an error deleting the user:', error);
      });
  };

  return (
    <div className="UserList">
      <h1>Users List</h1>
      <table>
        <thead>
          <tr>
            <th onClick={() => handleSort('id')}>ID</th>
            <th onClick={() => handleSort('name')}>Name</th>
            <th onClick={() => handleSort('birth_year')}>Birth Year</th>
            <th onClick={() => handleSort('state')}>State</th>
            <th onClick={() => handleSort('email')}>Email</th>
            <th>Notes</th>
            <th>Telephone</th>
            <th>Actions</th> {/* Added Actions column */}
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id}>
              <td>{user.id}</td>
              <td>{user.name}</td>
              <td>{user.birth_year}</td>
              <td>{user.state}</td>
              <td>{user.email}</td>
              <td>{user.notes || "No notes"}</td>
              <td>{user.tel || "No telephone"}</td>
              <td>
                <button onClick={() => handleDelete(user.id)}>Delete</button> {/* Delete Button */}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default UserList;
