import React, { useState, useEffect } from 'react'
import axios from "axios"

import './App.css';

function App() {
  const [product, setProduct] = useState([]); // State for storing product list
  const [isModalOpen, setIsModalOpen] = useState(false); // State for modal visibility

  // Function to open the modal
  const openPopup = () => {
    setIsModalOpen(true);
  };

  // Function to close the modal
  const handleClose = () => {
    setIsModalOpen(false);
  };

  // Function to fetch all products from the backend
  const getAllProduct = async () => {
    try {
      const res = await axios.get("http://localhost:3005/product"); // Make sure the backend URL is correct
      setProduct(res.data); // Update the state with the fetched data
    } catch (error) {
      console.error("Error fetching products:", error); // Log error for debugging
    }
  };

  // UseEffect to fetch data when the component mounts
  useEffect(() => {
    getAllProduct();
  }, []);
  return (
    <div>
    <h1>Product List</h1>
    <button onClick={openPopup}>Open Modal</button>
    {isModalOpen && (
      <div>
        <h2>Modal Content</h2>
        <button onClick={handleClose}>Close Modal</button>
      </div>
    )}
    <ul>
      {product.map((item) => (
        <li key={item.id}>
          {item.name} - {item.price}
        </li>
      ))}
    </ul>
  </div>
  );
};



export default App;
